
# install the RevGadgets R package from:
# https://github.com/revbayes/RevGadgets

library(RevGadgets)

# plot the ancestral states tree generated by RevBayes
p = plot_ancestral_states("results/revbayes_ancestral_states.tree", 
                          summary_statistic="MAP", 
                          node_label_size=0,
                          tip_label_size=3, 
                          tip_label_offset=0.4,
                          tip_label_italics=TRUE,
                          alpha=.3)


# set the final dimensions and write pdf
p = p + coord_cartesian(xlim = c(0, 20))
print(p)
ggsave("results/revbayes_ancestral_states.pdf", width = 11, height = 9)
